void main(void);void main(void){	RGBColor theColor={0, 0, 0};	WindowPtr	theWindow;	Rect	theCircle;	int		l=100, t=100, r=300, b=300;	unsigned int c;		InitGraf(&qd.thePort);	InitFonts();	InitWindows();	InitMenus();	TEInit();	InitDialogs(0L);	FlushEvents(everyEvent, 0);	InitCursor();	MaxApplZone();		theWindow=GetNewCWindow(128, 0L, (WindowPtr)-1L);	SetPort(theWindow);		for(c=0; c<=65000; c+=750)	{		SetRect(&theCircle, l, t, r, b);		theColor.blue=c;		RGBForeColor(&theColor);		PaintOval(&theCircle);		l++;		t++;		r--;		b--;	}		while(!Button())		;}